// src/components/ContactPopup.js
import React, { useState } from 'react';
import '.portfolio-frontend\src\pages\css\app.css';

const ContactPopup = () => {
    const [isPopupOpen, setIsPopupOpen] = useState(false);

    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    return (
        <>
            {/* Pop-Up Button */}
            <div className="popup-button" onClick={togglePopup}>
                Contact Info
            </div>

            {/* Pop-Up Content */}
            {isPopupOpen && (
                <div className="popup-content">
                    <p>Phone: 404-907-9942</p>
                    <p>
                        LinkedIn: <a href="https://linkedin.com/in/larson-evans-72687b178/" target="_blank" rel="noopener noreferrer">linkedin.com/in/larson-evans-72687b178/</a>
                    </p>
                    <p>Email: larsonevans21@gmail.com</p>
                </div>
            )}
        </>
    );
};

export default ContactPopup;
